if ([intptr]::size -eq 8) {    $mongoDriverPath = (Get-ItemProperty "HKLM:\SOFTWARE\Wow6432Node\Microsoft\.NETFramework\v3.5\AssemblyFoldersEx\MongoDB CSharpDriver 1.1").'(default)';}else {    $mongoDriverPath = (Get-ItemProperty "HKLM:\SOFTWARE\Microsoft\.NETFramework\v3.5\AssemblyFoldersEx\MongoDB CSharpDriver 1.1").'(default)';}Add-Type -Path "$($mongoDriverPath)\MongoDB.Bson.dll";Add-Type -Path "$($mongoDriverPath)\MongoDB.Driver.dll";$db = [MongoDB.Driver.MongoDatabase]::Create('mongodb://localhost/Eventos');$collection = $db['sec'];$hora=get-date;get-eventlog -log "security" -before $hora -after $hora.AddMinutes(-75)| ForEach-Object { [MongoDB.Bson.BsonDocument] $doc = @{    "_id"= [MongoDB.Bson.ObjectId]::GenerateNewId();    "Mensaje"= $_.Message.tostring();    "Origen"= $_.Source.tostring();    "Hora"=$_.TimeGenerated;    "Eventid"=$_.eventid.tostring();   };$collection.Insert($doc);write-host "instertado: "$doc.tostring(); };